import { CREATE_PICTURE } from '../constants/types'
import jimp from 'jimp'
import { cv } from 'myOpencv'
const { createCanvas, loadImage } = require('canvas')

export const createPicture = url => dispatch => {
    try {

        loadImage('examples/images/lime-cat.jpg').then((image) => {
            let src = cv.imread(url)
            console.log(src)
        })

        dispatch({
            type: CREATE_PICTURE,
            payload: url
        })

    } catch (error) {
        console.log(error);

    }
}
