import React,{useEffect} from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import { useDispatch, useSelector } from 'react-redux'
import { getImageEdit } from '../../redux/actions/picture'
import isEmp from '../../validation'

const useStyles = makeStyles({
    root:{
        width:'70%'
    }
})

export const Picture = (props) => {
  
    const {id} = props

    const dispatch = useDispatch()

    useEffect(()=>{
        dispatch(getImageEdit(id))
    },id)

    const {imageEdit} = useSelector(state=>state.picture)

    const createImage = ()=>{
        if(!isEmp(imageEdit))
        {
            console.log(imageEdit.src);
            
            return (<img src={imageEdit.url}></img> )
        }
    }

    const classes = useStyles()

    return (
        <Grid className={classes.root} >
            <Grid className={classes.img}>
                {createImage()}
            </Grid>
        </Grid>
    )
}
