const cv = require('./opencv')
const { Canvas, createCanvas, Image, ImageData, loadImage, createImageData } = require('canvas')

const color2Gray = async url => {

    const image = await loadImage(url)

    const src = cv.imread(image)

    const mydata = createImageData(new Uint8ClampedArray(src.data8S), src.rows, src.cols)

    console.log(mydata)

    const img = new Image()

    img.onload = () => ctx.drawImage(img, 0, 0)

    img.onerror = err => { throw err }

    img.src = mydata

    const canvas = createCanvas(300, 300)

    cv.imshow(canvas, src)

    console.log(canvas)


}

module.exports = {
    color2Gray
}
