const cv = require('./opencv')
const { createCanvas, Image , loadImage, createImageData } = require('canvas')

const color2Gray = async url => {

    const image = await loadImage(url)

    const src = cv.imread(image)

    const mydata = createImageData(new Uint8ClampedArray(src.data8S), src.rows, src.cols)

    console.log(mydata)

    const canvas = createCanvas(src.rows, src.cols)

    let ctx = canvas.getContext('2d')

    ctx.putImageData(mydata ,src.rows, src.cols)

    return canvas
}

module.exports = {
    color2Gray
}
