import { CREATE_PICTURE } from '../constants/types'
import {useDispatch, useSelector} from 'react-redux'
import jimp from 'jimp'

export const createPicture = picture =>{
    try {

        jimp.read(picture)
            .then(lenna => {
                console.log(lenna)
            })
            .catch(err => {
                console.error(err)
            })

        return ({
            type:CREATE_PICTURE,
            payload:picture
        })
    } catch (error) {
        
    }
}
