import { CREATE_PICTURE } from '../constants/types'
import jimp from 'jimp'
const { createCanvas, createImageData } = require('canvas')



export const createPicture = url => async dispatch => {
    try {
        
        let data = (await jimp.read(url)).bitmap

        const canvas = createImageData(data.data,data.width, data.height)

        console.log(canvas)
        
        dispatch({
            type: CREATE_PICTURE,
            payload: url
        })

    } catch (error) {

    }
}
