import { CREATE_PICTURE,
         GET_IMAGE_EDIT ,
         TEST
} from '../constants/types'

const initialState = {
    images:[],
    imageEdit:{},
    text:{}
}

export default (state = initialState, action) => {

    switch (action.type) {
        case CREATE_PICTURE:{
            state.images.push(action.payload)
            return{
                ...state
            }
        }
        case GET_IMAGE_EDIT:{
            const id = action.payload
            const image = state.images.find(t=>t.id === id)

            if(image)
            {
                return{
                    ...state,
                    imageEdit:image
                }
            }
            else
                return{
                    ...state,
                    imageEdit:{}
                }
        }
        case TEST:{
                return {
                    ...state,
                    text:action.payload
                }
        }
        default:
            return state
    }

}
