import React,{useEffect} from 'react'
import { Tool } from '../../container/Tool'
import { Picture } from '../../container/Picture'
import { Sidebar } from '../../container/Sidebar'
import Grid from '@material-ui/core/Grid'
import {Redirect} from 'react-router-dom'
import { useDispatch,useSelector } from 'react-redux'
import { getImageEdit } from '../../redux/actions/picture'

export const Edit = (props) => {
    
    const id = props.match.params.id

    const dispatch = useDispatch()

    useEffect(()=>{
        dispatch(getImageEdit(id))
    },[id])

    const {imageEdit} = useSelector(state=>state.picture)

    if(!imageEdit)
        return <Redirect to='/' />;
    
    return (

        <Grid
            container
            direction="row"
            justify="flex-start"
            alignItems="flex-start"
        >
            <Sidebar />
            <Tool />
            <Picture src={imageEdit} />
        </Grid>
    )
}
