import { CREATE_PICTURE,GET_IMAGE_EDIT } from '../constants/types'
import jimp from 'jimp'
import { color2Gray } from 'opencv'
import { v4 } from 'uuid'
const { createCanvas, loadImage } = require('canvas')


let id =''

export const createPicture = url =>  dispatch => {
    try {
        color2Gray(url)
        id = v4()

        dispatch({
            type: CREATE_PICTURE,
            payload: {url,id}
        })

    } catch (error) {
        console.log(error);

    }
}

export const getImageEdit = id => dispatch=>{
    try {
        dispatch({
            type:GET_IMAGE_EDIT,
