import React, { useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid'
import { useSelector } from 'react-redux'
import isEmp from '../../validation'
import ima from './nam.jpg'

const useStyles = makeStyles({
    root: {
        width: '78%',
        flexGrow: 1
    },
    img: {
        width: '80%'
    },
    imgContent: {
        width: '60%',
        marginTop:'auto',
        marginBottom:'auto'
    }
})

export const Picture = (props) => {

    const { imageEdit } = useSelector(state => state.picture)

    const classes = useStyles()

    const createImage = () => {
        if (!isEmp(imageEdit)) {
            return (<img src={imageEdit.url} className={classes.imgContent}></img>)
        }
    }


    return (
        <Grid
            className={classes.root}
            container
            direction="row"
            justify="center"
            alignItems="center">
            <div>
            <img src={ima} className={classes.imgContent} />
            </div>
        </Grid>
    )
}
