import { CREATE_PICTURE,
         GET_IMAGE_EDIT 
} from '../constants/types'

const initialState = {
    images:[],
    imageEdit:{}
}

export default (state = initialState, action) => {

    switch (action.type) {
        case CREATE_PICTURE:{
            state.images.push(action.payload)
            return{
                ...state
            }
        }
        case GET_IMAGE_EDIT:{
            const id = action.payload
            const image = state.images.find(t=>t.id === id)
            console.log(image);
            
        
            if(!image)
            {
                return{
                    ...state,
                    imageEdit:image
                }
            }
            else
                return{
                    ...state,
                    imageEdit:{}
                }
        }
        default:
            return state
    }

}
